import React, { Component } from 'react'
import axios from 'axios'

export default class CreateClasification extends Component {

    state = {
        clasifications: [],
        califName: ''
    }

    async componentDidMount() {
        const res = await axios.get('http://localhost:3001/api/clasif')
        this.setState({clasifications: res.data})
        console.log(this.state.clasifications)
    }

    onChangeClasification = (e) => {
        this.setState({
            clasification: e.target.value
        })
        
    }

    onSubmit = (e) => {
        e.preventDefault()
    }

    render() {
        return (
            <div className="row">
                <div className="col-md-8">
                    <ul className="list-group">
                        {
                            this.state.clasifications.map(clasif => (
                                <li className="list-group-item list-group-item-action" key={clasif._id}>
                                    { clasif.name }
                                </li>)
                            )
                        }
                    </ul>
                </div>
                <div className="col-md-4">
                    <div className="card card-body">
                        <h4>Agregar Clasificaci√≥n</h4>
                    </div>
                    <form onSubmit={this.onSubmit}>
                        <div className="form-group">
                            <input
                                type="text"
                                className="form-control"
                                onChange={this.onChangeClasification}
                                />
                        </div>
                        <button type="submit" className="btn btn-primary">
                            Guardar
                        </button>
                    </form>
                </div>
            </div>
        )
    }
}
