import React, { Component } from 'react'
import axios from 'axios'

export default class CreateMovie extends Component {

    state = {
        clasifications: [],
        newMovieName: '',
        newDirector: '',
        newClasif: ''
    }

    async componentDidMount() {
        const res = await axios.get('http://localhost:3001/api/clasif')
        this.setState({clasifications: res.data})
    }

    onSubmit = (e) => {
        e.preventDefault()
    }

    onCalifChange = (e) => {
        console.log(e.target.value)
    }

    render() {
        return (
            <div className='col-md-6 offset-md-3'>
                <div className='card card-body'>
                    <h4>Cargar Nueva Pel√≠cula</h4>
                    <form onSubmit={this.onSubmit}>
                                {/*    {movie.name}
                                    {movie.director}
                                    {movie.clasification}
                                */}

                        {/* Select Clasification */}
                        <div className="form-group">
                            <select
                                className="form-control"
                                name="clasifSelect"
                                onChange={this.onCalifChange}
                            >
                                {
                                    this.state.clasifications.map(clasif => 
                                    <option key={clasif.id} value={clasif.name}>
                                        {clasif.name}
                                    </option>)
                                }
                            </select>
                        </div>
                        <button type="submit" className="btn btn-primary">
                            Guardar
                        </button>
                    </form>
                </div>
            
            </div>
        )
    }
}
