import React, { Component } from 'react'
import axios from 'axios'

export default class CreateMovie extends Component {

    state = {
        clasifications: [],
        newMovieName: '',
        newDirector: '',
        newClasif: ''
    }

    async componentDidMount() {
        const res = await axios.get('http://localhost:3001/api/clasif')
        this.setState({clasifications: res.data})
    }

    onSubmit = async (e) => {
        const newMovie = {
            name: this.state.newMovieName,
            director: this.state.newDirector,
            clasification: this.state.newClasif
        }
        const res = await axios.post('http://localhost:3001/api/movies', newMovie)
        console.log(res)
        e.preventDefault()
    }

    onInputChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        return (
            <div className='col-md-6 offset-md-3'>
                <div className='card card-body'>
                    <h4>Cargar Nueva Película</h4>
                    <form onSubmit={this.onSubmit}>
                        <div className="form-group">
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Título"
                                name="newMovieName"
                                onChange={this.onInputChange}
                                required
                                />
                        </div>
                        <div className="form-group">
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Director"
                                name="newDirector"
                                onChange={this.onInputChange}
                                required
                                />
                        </div>

                        {/* Select Clasification */}
                        <div className="form-group">
                            <select
                                className="form-control"
                                name="newClasif"
                                onChange={this.onInputChange}
                            >
                                <option value="">Seleccione un Clasificación</option>
                                {
                                    this.state.clasifications.map(clasif => 
                                    <option key={clasif.id} value={clasif.name}>
                                        {clasif.name}
                                    </option>)
                                }
                            </select>
                        </div>
                        <button type="submit" className="btn btn-primary">
                            Guardar
                        </button>
                    </form>
                </div>
            
            </div>
        )
    }
}
